SELECT ZZ.ID,ZZ.NAME,B.name AS DEPTNAMEï¼ŒZZ.TYPE AS ROLETYPE ,YY.NAME AS TEAMNAME,YY.CODE AS TEAMCODE,YY.ID AS TEAMID,A.WORK_NUMBER FROM ST_GZRW XX LEFT JOIN ST_GZRWXZ YY ON YY.GZRW_ID=XX.ID LEFT JOIN ST_GZRWXZCY ZZ ON ZZ.GZRWXZ_ID=YY.ID LEFT JOIN CHECKMIS.SYS_PERSON A ON ZZ.WORK_NUMBER=A.WORK_NUMBER LEFT JOIN CHECKMIS.SYS_DEPT B ON A.DEPT_ID=B.ID WHERE ZZ.NAME IS NOT NULL AND XX.ID=#{gzrwId}
